              <a-input
                v-model:value="invoice.userProfileData.address1"
                type="text"
                placeholder="Enter Address"
                class="w-full border"
              /><span
                class="text-red-500 text-md font-small text-xs"
                v-if="proceedClicked && errors.address1"
                >*{{ errors.address1 }}</span
              >
            </div>
             <div>{{  }}
              <p class="justify-start flex text-md font-medium"> <span class="text-[#ff0000]">*</span>Country:</p>
              <a-select
                v-model:value="invoice.userProfileData.country"
                class="w-full "
              >
                <a-select-option
                  v-for="country in invoice.countryOptions"
                  :key="country.value"
                  :value="country.value"
                >
                  {{ country.value }}
                </a-select-option>
              </a-select><span
                class="text-red-500 text-md font-small text-xs"
